"use strict";

var m, divId, initLatitude, initLongitude, map, $body = $("body");

$(window).on("load", function() {
    $body.addClass("loaded");
});

if ($body.attr("data-preloader") === "true") {
    $body.append($("<div class='preloader'><div><span>L</span><span>O</span><span>A</span><span>D</span><span>I</span><span>N</span><span>G</span></div></div>"));
}

var customCursor = document.getElementById("cursor");
if (customCursor) {
    var a = document.getElementById("cursor");
    document.addEventListener("mousemove", function(e) {
        a.style.left = e.pageX + "px";
        a.style.top = e.pageY + "px";
    });
    document.querySelectorAll("a, button, input, textarea, .cursor-link").forEach(function(e) {
        e.addEventListener("mouseenter", function() {
            a.classList.add("scale-cursor");
        });
        e.addEventListener("mouseleave", function() {
            a.classList.remove("scale-cursor");
        });
    });
}

var headerNav = $(".nav-box");
if (headerNav.length) {
    var s = $("#nav-toggle");
    s.on("click", function() {
        if (headerNav.hasClass("show")) {
            headerNav.removeClass("show");
            s.removeClass("active");
        } else {
            headerNav.addClass("show");
            s.addClass("active");
        }
    });
    $(document).on("click", function(e) {
        if ($(e.target).closest(".nav-box, #nav-toggle").length === 0 && headerNav.hasClass("show")) {
            headerNav.removeClass("show");
            s.removeClass("active");
        }
    });
}

var scrollTopBtn = document.querySelector(".scrolltotop");
if (scrollTopBtn) {
    window.addEventListener("scroll", function() {
        if (window.pageYOffset > 700) {
            scrollTopBtn.classList.add("show");
        } else {
            scrollTopBtn.classList.remove("show");
        }
    });
}

