var $lightboxImage = $(".lightbox-image-box");
$lightboxImage.each(function() {
    $(this).magnificPopup({
        type: "image",
        fixedContentPos: false,
        removalDelay: 200,
        closeOnContentClick: true,
        image: {
            titleSrc: "data-image-title"
        }
    });
});

var $lightboxMedia = $(".lightbox-media-box");
$lightboxMedia.each(function() {
    $(this).magnificPopup({
        type: "iframe",
        fixedContentPos: false,
        removalDelay: 200,
        preloader: false,
        iframe: {
            patterns: {
                youtube: {
                    index: "youtube.com/",
                    id: "v=",
                    src: "//www.youtube.com/embed/%id%?autoplay=1&rel=0"
                },
                vimeo: {
                    index: "vimeo.com/",
                    id: "/",
                    src: "//player.vimeo.com/video/%id%?autoplay=1"
                }
            },
            srcAction: "iframe_src"
        }
    });
});

var mapCanvas = $(".gmap");
if (mapCanvas.length) {
    for (var i = 0; i < mapCanvas.length; i++) {
        initLatitude = (m = mapCanvas[i]).dataset.latitude;
        initLongitude = m.dataset.longitude;
        divId = "#" + m.id;
        map = new GMaps({
            el: divId,
            lat: initLatitude,
            lng: initLongitude,
            zoom: 16,
            scrollwheel: false,
            styles: []
        });
        map.addMarker({
            lat: initLatitude,
            lng: initLongitude
        });
    }
}

$("#contactform").on("submit", function(e) {
    var a = $("#name").val(),
        s = $("#email").val(),
        t = $("#subject").val(),
        o = $("#message").val();

    if ("" === a) $("#name").addClass("error-color");
    if ("" === s) $("#email").addClass("error-color");
    if ("" === t) $("#subject").addClass("error-color");
    if ("" === o) {
        $("#message").addClass("error-color");
    } else {
        $.ajax({
            url: "assets/php/contact-form.php",
            data: $(this).serialize(),
            type: "POST",
            success: function(e) {
                $("#success").addClass("show-result");
                $("#contactform").each(function() {
                    this.reset();
                });
            },
            error: function(e) {
                $("#error").addClass("show-result");
            }
        });
        $("#contactform input, #contactform textarea").removeClass("error-color");
    }
    e.preventDefault();
});